INSERT [dbo].[news] ([news_id], [news_category_id], [creator_id], [news_title], [news_content], [news_image], [news_hashtag], [updated_id], [created_date], [is_deleted]) VALUES (2, 3, 1, N'Bệnh lý tim mạch: Dấu hiệu và cách phòng tránh', N'<blockquote class="blockquote my-4 size-x font-weight-bold" style="box-sizing: inherit; padding-top: 0.5rem; padding-right: 1rem; padding-bottom: 0.5rem; border-left-width: 0.25rem; border-left-color: #eceeef; font-weight: bold; color: #292b2c; font-family: -apple-system, system-ui, BlinkMacSystemFont, ''Segoe UI'', Roboto, ''Helvetica Neue'', Arial, sans-serif; background-color: #ffffff; font-size: 0.9em !important; margin: 1.5rem !important 0px 1.5rem !important 0px;">
<p class="mb-0" style="box-sizing: inherit; margin-top: 0px; margin-bottom: 0px !important;">Tim mạch l&agrave; bệnh l&yacute; xuất hiện &acirc;m thầm nhưng để lại nhiều hậu quả nghi&ecirc;m trọng đối với t&iacute;nh mạng. Trong những năm trở lại đ&acirc;y, tỷ lệ tử vong do c&aacute;c bệnh tim mạch ng&agrave;y một tăng ở c&aacute;c nước đang ph&aacute;t triển, trong đ&oacute; c&oacute; Việt Nam. Hiện nay, trung b&igrave;nh cứ 4 người lớn ở Việt Nam, c&oacute; &iacute;t nhất 1 &ndash; 2 người đ&atilde; mang nguy cơ mắc bệnh tim mạch. Vậy bệnh tim mạch l&agrave; g&igrave;? Dấu hiệu sớm nhất v&agrave; c&aacute;ch ph&ograve;ng ngừa như thế n&agrave;o?</p>
</blockquote>
<p><img class="img-fluid img-100" style="box-sizing: inherit; border-style: none; vertical-align: middle; max-width: 100%; height: auto; width: 730px; color: #292b2c; font-family: -apple-system, system-ui, BlinkMacSystemFont, ''Segoe UI'', Roboto, ''Helvetica Neue'', Arial, sans-serif; font-size: 16px; background-color: #ffffff;" src="https://careplusvn.com/Uploads/t/be/benh-tim-mach-2_0003323_710.jpeg" alt="Bệnh tim mạch l&agrave; g&igrave;? Dấu hiệu sớm nhất v&agrave; c&aacute;ch điều trị" /></p>
<div class="row my-3" style="box-sizing: inherit; display: flex; flex-wrap: wrap; color: #292b2c; font-family: -apple-system, system-ui, BlinkMacSystemFont, ''Segoe UI'', Roboto, ''Helvetica Neue'', Arial, sans-serif; font-size: 16px; background-color: #ffffff; margin: 1rem !important -15px 1rem !important -15px;">
<div class="col-lg-5 col-sm-12" style="box-sizing: inherit; position: relative; width: 316.656px; min-height: 1px; padding-right: 15px; padding-left: 15px; flex: 0 0 41.6667%; max-width: 41.6667%;">
<div style="box-sizing: inherit;">
<div class="doc-profile mt-2 d-flex flex-row justify-content-between" style="box-sizing: inherit; display: flex !important; flex-direction: row !important; justify-content: space-between !important; margin-top: 0.5rem !important;">
