INSERT [dbo].[news] ([news_id], [news_category_id], [creator_id], [news_title], [news_content], [news_image], [news_hashtag], [updated_id], [created_date], [is_deleted]) VALUES (8, 7, 1, N'Nên tiêm vắc xin sốt xuất huyết ở đâu? Tiêm bao nhiêu mũi là đủ?', N'<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Theo Tổ chức y tế thế giới (WHO) đăng tr&ecirc;n tạp ch&iacute; ng&agrave;y 23 th&aacute;ng 4 năm 2024, mỗi năm, tr&ecirc;n thế giới c&oacute; khoảng 3,9 tỷ người bị ảnh hưởng bởi virus sốt xuất huyết, trong đ&oacute; khoảng 100 - 400 triệu người mắc bệnh. Việc ti&ecirc;m vắc xin ph&ograve;ng ngừa sốt xuất huyết cũng thu h&uacute;t sự ch&uacute; &yacute; từ nhiều quốc gia, đặc biệt trong những giai đoạn b&ugrave;ng ph&aacute;t dịch. V&igrave; vậy h&atilde;y c&ugrave;ng t&igrave;m hiểu xem n&ecirc;n ti&ecirc;m vắc xin sốt xuất huyết ở đ&acirc;u v&agrave; ti&ecirc;m bao nhi&ecirc;u mũi l&agrave; đủ th&ocirc;ng qua b&agrave;i viết dưới đ&acirc;y.</p>
<h2 dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Vắc xin sốt xuất huyết l&agrave; g&igrave;?</h2>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Vắc xin sốt xuất huyết l&agrave; loại vắc xin được ph&aacute;t triển để ngăn ngừa bệnh sốt xuất huyết do&nbsp;<a style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; touch-action: manipulation; text-decoration: inherit; word-break: break-word; cursor: pointer;" href="https://nhathuoclongchau.com.vn/bai-viet/dengue-virus-la-gi-dengue-virus-gay-ra-benh-sot-xuat-huyet-co-may-loai.html">virus Dengue</a>&nbsp;g&acirc;y ra. Vắc xin n&agrave;y gi&uacute;p cơ thể k&iacute;ch th&iacute;ch chủ động tạo ra miễn dịch đặc hiệu nhằm chống lại 4 chủng virus Dengue (DEN-1, DEN-2, DEN-3, DEN-4). Mục ti&ecirc;u của vắc xin l&agrave; giảm nguy cơ mắc bệnh, giảm thiểu c&aacute;c biến chứng nguy hiểm, giảm tỷ lệ phải nhập viện v&agrave; g&oacute;p phần tạo được miễn dịch cộng đồng.</p>
<figure class="image" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 20px 0px; word-break: break-word; width: 597px; color: #020b27; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; font-size: 16px; background-color: #ffffff;"><img style="box-sizing: border-box; border: 0px none #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; vertical-align: middle; display: block; max-width: unset; height: auto; word-break: break-word; width: calc(100% + 56px); margin: 20px -28px 12px; border-radius: 14px;" src="https://cdn.nhathuoclongchau.com.vn/unsafe/800x0/https://cms-prod.s3-sgn09.fptcloud.com/nen_tiem_vac_xin_sot_xuat_huyet_o_dau_tiem_bao_nhieu_mui_la_du_1_eb29a9b6ab.jpg" alt="N&ecirc;n ti&ecirc;m vắc xin sốt xuất huyết ở đ&acirc;u? Ti&ecirc;m bao nhi&ecirc;u mũi l&agrave; đủ? 1" />
<figcaption style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word; color: var(--text-color); font-size: 13px; line-height: 18px;"><em style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word;">Vắc xin sốt xuất huyết Dengue l&agrave; vắc xin tại miễn dịch chủ động ph&ograve;ng bệnh sốt xuất huyết Dengue</em></figcaption>
</figure>
<h2 dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">V&igrave; sao cần ti&ecirc;m ph&ograve;ng sốt xuất huyết?</h2>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Sốt xuất huyết Dengue l&agrave; một bệnh truyền nhiễm cấp t&iacute;nh do virus Dengue g&acirc;y ra, l&acirc;y truyền từ người sang người qua trung gian muỗi Aedes Aegypti (th&ocirc;ng qua vết cắn, đốt của muỗi). Bệnh được đặc trưng bởi triệu chứng&nbsp;<a style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; touch-action: manipulation; text-decoration: inherit; word-break: break-word; cursor: pointer;" href="https://nhathuoclongchau.com.vn/benh/sot-618.html">sốt</a>, xuất huyết v&agrave; tho&aacute;t dịch, huyết tương, c&oacute; thể dẫn đến sốc v&agrave; tử vong nếu kh&ocirc;ng được điều trị kịp thời v&agrave; đ&uacute;ng c&aacute;ch.</p>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">T&aacute;c nh&acirc;n g&acirc;y bệnh l&agrave; virus Dengue, c&oacute; 4 type huyết thanh (D1, D2, D3, D4). Tại Việt Nam, cả 4 type virus đều lưu h&agrave;nh, nhưng type 2 l&agrave; phổ biến nhất. Muỗi Aedes aegypti l&agrave; trung gian truyền bệnh ch&iacute;nh, ph&acirc;n bố rộng r&atilde;i v&agrave; thường sống ở những khu vực c&oacute; nước đọng hoặc b&ugrave;n lầy. Lo&agrave;i muỗi n&agrave;y chủ yếu hoạt động v&agrave;o s&aacute;ng sớm v&agrave; chiều tối.</p>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Ti&ecirc;m vắc xin ph&ograve;ng sốt xuất huyết l&agrave; cần thiết v&igrave; muỗi Aedes, vật chủ trung gian truyền bệnh, c&oacute; khả năng sinh sản nhanh ch&oacute;ng v&agrave; sống l&acirc;u. Một con muỗi c&aacute;i Aedes c&oacute; thể đẻ từ 100 đến 200 trứng mỗi lần v&agrave; trong điều kiện m&ocirc;i trường thuận lợi, trứng sẽ nở trong khoảng 2 ng&agrave;y. Theo Trung t&acirc;m kiểm so&aacute;t v&agrave; ph&ograve;ng ngừa dịch bệnh CDC, từ giai đoạn ấu tr&ugrave;ng đến khi trở th&agrave;nh muỗi trưởng th&agrave;nh chỉ mất khoảng 7 ng&agrave;y. Muỗi c&aacute;i c&ograve;n c&oacute; tuổi thọ k&eacute;o d&agrave;i đến 40 ng&agrave;y, trong suốt thời gian n&agrave;y, n&oacute; c&oacute; thể l&acirc;y bệnh cho nhiều người.</p>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Ở Việt Nam, bệnh lưu h&agrave;nh rất phổ biến, ở cả 4 miền Bắc, Trung, Nam v&agrave; T&acirc;y Nguy&ecirc;n, kể cả ở th&agrave;nh thị v&agrave; v&ugrave;ng n&ocirc;ng th&ocirc;n, bệnh xảy ra quanh năm nhưng thường b&ugrave;ng ph&aacute;t th&agrave;nh dịch lớn v&agrave;o m&ugrave;a mưa, nhất l&agrave; v&agrave;o c&aacute;c th&aacute;ng 7, 8, 9, 10. Do đ&oacute;, việc ti&ecirc;m vắc xin l&agrave; biện ph&aacute;p quan trọng, đặc hiệu nhất để giảm nguy cơ mắc bệnh.</p>
<figure class="image" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 20px 0px; word-break: break-word; width: 597px; color: #020b27; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; font-size: 16px; background-color: #ffffff;"><img style="box-sizing: border-box; border: 0px none #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; vertical-align: middle; display: block; max-width: unset; height: auto; word-break: break-word; width: calc(100% + 56px); margin: 20px -28px 12px; border-radius: 14px;" src="https://cdn.nhathuoclongchau.com.vn/unsafe/https://cms-prod.s3-sgn09.fptcloud.com/nen_tiem_vac_xin_sot_xuat_huyet_o_dau_tiem_bao_nhieu_mui_la_du_2_29ef4a29ca.png" alt="N&ecirc;n ti&ecirc;m vắc xin sốt xuất huyết ở đ&acirc;u? Ti&ecirc;m bao nhi&ecirc;u mũi l&agrave; đủ? 2" />
<figcaption style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word; color: var(--text-color); font-size: 13px; line-height: 18px;"><em style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word;">Sốt xuất huyết c&oacute; thể g&acirc;y ra nhiều biến chứng nghi&ecirc;m trọng v&igrave; vậy việc ti&ecirc;m ph&ograve;ng vắc xin l&agrave; cần thiết để ph&ograve;ng bệnh</em></figcaption>
</figure>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Ngo&agrave;i ra, khi bị sốt xuất huyết thể nặng, người bệnh c&oacute; thể gặp nhiều biến chứng nghi&ecirc;m trọng như sốc do mất m&aacute;u hoặc tho&aacute;t huyết tương... đe dọa đến t&iacute;nh mạng. Vi&ecirc;m đường h&ocirc; hấp cũng c&oacute; thể xảy ra do tho&aacute;t huyết tương tr&agrave;n v&agrave;o phổi, trong khi xuất huyết n&atilde;o c&oacute; thể g&acirc;y tụt huyết &aacute;p đột ngột. Ngo&agrave;i ra, suy tim v&agrave; suy thận c&oacute; thể xảy ra do xuất huyết k&eacute;o d&agrave;i, khiến tim kh&ocirc;ng đủ tuần ho&agrave;n v&agrave; thận phải l&agrave;m việc qu&aacute; sức. Một số bệnh nh&acirc;n c&ograve;n c&oacute; nguy cơ m&ugrave; đột ngột do xuất huyết v&otilde;ng mạc. Đặc biệt, phụ nữ mang thai bị sốt xuất huyết c&oacute; thể sốt cao v&agrave; gặp nguy cơ giảm tiểu cầu, đe dọa sức khỏe thai nhi, nhất l&agrave; trong những th&aacute;ng đầu thai kỳ.</p>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Việc ti&ecirc;m vắc xin sẽ gi&uacute;p cơ thể tạo ra miễn dịch đặc hiệu, từ đ&oacute; giảm nguy cơ l&acirc;y nhiễm virus v&agrave; mắc bệnh, bảo vệ sức khỏe bản th&acirc;n cũng như cộng đồng.</p>
<h2 dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Ai cần ti&ecirc;m ph&ograve;ng vắc xin sốt xuất huyết?</h2>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Theo WHO, việc ti&ecirc;m vắc xin ph&ograve;ng sốt xuất huyết c&oacute; thể &aacute;p dụng cho trẻ em v&agrave; người lớn, t&ugrave;y thuộc v&agrave;o t&igrave;nh h&igrave;nh dịch tễ v&agrave; g&aacute;nh nặng bệnh tật của từng quốc gia, đặc biệt ở những nh&oacute;m d&acirc;n số dễ bị ảnh hưởng nặng.</p>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Vắc xin sốt xuất huyết n&ecirc;n được ti&ecirc;m cho những người sau:</p>
<ul style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 0px 0px 1em 24px; list-style-position: initial; list-style-image: initial; padding: 0px; word-break: break-word; color: #020b27; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; font-size: 16px; background-color: #ffffff;">
<li dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word; line-height: 24px; margin-top: 8px; color: var(--text-color); overflow-wrap: break-word; margin-bottom: 8px;" aria-level="1">Trẻ em từ 4 tuổi trở l&ecirc;n: Đặc biệt ở những khu vực c&oacute; nguy cơ cao.</li>
<li dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word; line-height: 24px; margin-top: 8px; color: var(--text-color); overflow-wrap: break-word; margin-bottom: 8px;" aria-level="1">Người lớn: Nhất l&agrave; những người sống hoặc l&agrave;m việc trong v&ugrave;ng c&oacute; dịch.</li>
<li dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word; line-height: 24px; margin-top: 8px; color: var(--text-color); overflow-wrap: break-word; margin-bottom: 8px;" aria-level="1">Những người đ&atilde; từng mắc sốt xuất huyết: Vắc xin gi&uacute;p giảm nguy cơ t&aacute;i mắc bệnh nặng.</li>
</ul>
<p dir="ltr" style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Ngo&agrave;i ra, việc ti&ecirc;m vắc xin cũng cần được xem x&eacute;t dựa tr&ecirc;n t&igrave;nh h&igrave;nh dịch tễ của từng địa phương.</p>
