INSERT [dbo].[news] ([news_id], [news_category_id], [creator_id], [news_title], [news_content], [news_image], [news_hashtag], [updated_id], [created_date], [is_deleted]) VALUES (9, 7, 1, N'Những điều bạn cần biết trước khi quyết định tiêm vắc xin sốt xuất huyết', N'<h2 style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Sốt xuất huyết l&agrave; g&igrave; v&agrave; l&acirc;y truyền như thế n&agrave;o?</h2>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Sốt Dengue, hay c&ograve;n gọi l&agrave;&nbsp;<a style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; touch-action: manipulation; text-decoration: inherit; word-break: break-word; cursor: pointer;" href="https://nhathuoclongchau.com.vn/benh/sot-xuat-huyet-dengue-155.html">sốt xuất huyết Dengue</a>, l&agrave; một bệnh truyền nhiễm cấp t&iacute;nh c&oacute; nguy cơ b&ugrave;ng ph&aacute;t th&agrave;nh dịch. Muỗi&nbsp;<em style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; word-break: break-word;">Aedes Aegypti</em>&nbsp;l&agrave; lo&agrave;i trung gian truyền bệnh, l&acirc;y truyền virus Dengue thuộc 4 chủng kh&aacute;c nhau gồm DEN-1, DEN-2, DEN-3, v&agrave; DEN-4 từ muỗi sang người.</p>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;"><span style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; font-weight: bolder; word-break: break-word;">Lưu &yacute;:</span>&nbsp;Một con muỗi Aedes c&oacute; khả năng l&acirc;y truyền cho nhiều người v&igrave; ch&uacute;ng c&oacute; khả năng đốt nhiều lần. Thời gian virus c&oacute; thể tồn tại trong m&aacute;u người dao động từ 2 đến 7 ng&agrave;y.</p>
<h2 style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">C&aacute;c triệu chứng nghi ngờ mắc sốt xuất huyết</h2>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">C&aacute;c triệu chứng nghi ngờ khi người khoẻ mạnh mắc sốt xuất huyết thường chia th&agrave;nh ba giai đoạn l&acirc;m s&agrave;ng:</p>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;"><span style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; font-weight: bolder; word-break: break-word;">Giai đoạn sốt:&nbsp;</span>Bệnh nh&acirc;n đột ngột sốt cao li&ecirc;n tục, k&egrave;m theo c&aacute;c triệu chứng như nhức đầu, ch&aacute;n ăn, buồn n&ocirc;n. Quan s&aacute;t thấy da xung huyết, chảy m&aacute;u ch&acirc;n răng hoặc m&aacute;u cam.</p>
