INSERT [dbo].[news] ([news_id], [news_category_id], [creator_id], [news_title], [news_content], [news_image], [news_hashtag], [updated_id], [created_date], [is_deleted]) VALUES (15, 11, 1, N'Nguyên nhân sảy thai: Những yếu tố gây ảnh hưởng đến thai kỳ mẹ bầu cần biết', N'<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Sảy thai l&agrave; nỗi lo lớn của nhiều mẹ bầu khi mang thai, đặc biệt trong những tuần đầu thai kỳ. Hiểu r&otilde; c&aacute;c nguy&ecirc;n nh&acirc;n sảy thai l&agrave; bước quan trọng gi&uacute;p mẹ bầu c&oacute; kế hoạch chăm s&oacute;c sức khỏe tốt hơn, giảm thiểu c&aacute;c rủi ro tiềm ẩn. B&agrave;i viết n&agrave;y của Nh&agrave; thuốc Long Ch&acirc;u sẽ chia sẻ chi tiết về những yếu tố dẫn đến sảy thai v&agrave; c&aacute;ch ph&ograve;ng ngừa an to&agrave;n cho mẹ v&agrave; thai nhi.</p>
<h2 style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 16px 0px 8px; font-size: 20px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 28px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Nguy&ecirc;n nh&acirc;n sảy thai phổ biến m&agrave; mẹ bầu n&ecirc;n biết</h2>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;"><a style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; touch-action: manipulation; text-decoration: inherit; word-break: break-word; cursor: pointer;" href="https://nhathuoclongchau.com.vn/benh/say-thai-497.html">Sảy thai</a>&nbsp;l&agrave; hiện tượng thai nhi bị mất tự nhi&ecirc;n trước tuần 20 của thai kỳ v&agrave; đ&acirc;y l&agrave; một vấn đề y tế nghi&ecirc;m trọng m&agrave; nhiều phụ nữ mang thai c&oacute; thể gặp phải. Theo thống k&ecirc;, c&oacute; khoảng 10 - 20% c&aacute;c ca mang thai kết th&uacute;c bằng sảy thai v&agrave; hầu hết xảy ra trong tam c&aacute; nguyệt đầu ti&ecirc;n. Để gi&uacute;p mẹ bầu hiểu r&otilde; hơn về nguy&ecirc;n nh&acirc;n sảy thai, dưới đ&acirc;y l&agrave; những yếu tố phổ biến nhất dẫn đến t&igrave;nh trạng n&agrave;y.</p>
<h3 style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; font-size: 18px; word-break: break-word; color: #020b27; overflow-wrap: break-word; line-height: 24px; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff; margin: 12px !important 0px 8px 0px;">Sự bất thường về nhiễm sắc thể của thai nhi</h3>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">Một trong những nguy&ecirc;n nh&acirc;n sảy thai ch&iacute;nh l&agrave; sự bất thường về nhiễm sắc thể của thai nhi. Khi trứng hoặc tinh tr&ugrave;ng mang lỗi di truyền hoặc khi c&oacute; sự ph&acirc;n chia bất thường trong qu&aacute; tr&igrave;nh thụ thai, thai nhi c&oacute; thể kh&ocirc;ng ph&aacute;t triển b&igrave;nh thường v&agrave; dẫn đến sảy thai. Điều n&agrave;y thường xảy ra một c&aacute;ch tự nhi&ecirc;n v&agrave; đa phần những trường hợp sảy thai do nhiễm sắc thể bất thường kh&ocirc;ng thể ngăn ngừa được.</p>
<p style="box-sizing: border-box; border: 0px solid #e5e7eb; --tw-border-spacing-x: 0; --tw-border-spacing-y: 0; --tw-translate-x: 0; --tw-translate-y: 0; --tw-rotate: 0; --tw-skew-x: 0; --tw-skew-y: 0; --tw-scale-x: 1; --tw-scale-y: 1; --tw-scroll-snap-strictness: proximity; --tw-ring-offset-width: 0px; --tw-ring-offset-color: #fff; --tw-ring-color: rgba(59,130,246,.5); --tw-ring-offset-shadow: 0 0 #0000; --tw-ring-shadow: 0 0 #0000; --tw-shadow: 0 0 #0000; --tw-shadow-colored: 0 0 #0000; margin: 8px 0px; word-break: break-word; font-size: 16px; line-height: 24px; color: #020b27; overflow-wrap: break-word; font-family: __Inter_6493b6, __Inter_Fallback_6493b6; background-color: #ffffff;">C&aacute;c lỗi về nhiễm sắc thể c&oacute; thể bao gồm thừa hoặc thiếu nhiễm sắc thể (tam bội hoặc đơn bội), dẫn đến thai nhi kh&ocirc;ng thể tồn tại. Đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n phổ biến nhất g&acirc;y ra sảy thai trong tam c&aacute; nguyệt đầu ti&ecirc;n v&agrave; nhiều trường hợp mẹ bầu c&oacute; thể kh&ocirc;ng ph&aacute;t hiện được cho đến khi gặp phải biến chứng.</p>
